
def get_kmers(genome, k):
    kmers = [genome[i:i+k] for i in range(len(genome) - k + 1)]
    kmers.sort()
    return ' '.join(kmers)

def get_read_pairs(genome, k, d):
    L = 2 * k + d
    pairs = []

    for i in range(len(genome) - L + 1):
        window = genome[i:i+L]
        first_kmer = window[:k]
        second_kmer = window[k + d:k + d + k]
        pairs.append(f"({first_kmer},{second_kmer})")

    pairs.sort()
    return ' '.join(pairs)

genome = ""
k = 50
d = 100
with open("read_pairs_output.txt", "w") as f:
    f.write(get_read_pairs(genome, k, d))
with open("kmers_output.txt", "w") as f:
    f.write(get_kmers(genome, k))



